-- Escolho o banco para trabalhar
USE movies_data;

-- Apagar uma tabela 
DROP TABLE IF EXISTS movies;

-- Alguns exemplos de regras;
-- -> PRIMARY KEY único e obrigatório
-- not null (Não Vazio/item Obrigatório) 
-- Unique(Item único) 
-- Default(Se o campo estiver vazio ele preencherá automaticamente com o valor informado ao lado)
-- Drop (apagar um item)

-- Exemplo de como criar uma Tabela
CREATE TABLE IF NOT EXISTS movies (
    id_movie int(11) PRIMARY KEY AUTO_INCREMENT,
    year_film INT(4),
    year_ceremony INT(4),
    ceremony INT(4),
    category VARCHAR(512),
    `name` VARCHAR(512),
    film VARCHAR(512),
    winner VARCHAR(10)
);

